$('#eodReportLink').on('click',function(e){
    $.ajax({
            url: '/autofill-eod',
            type: 'GET',
            success: function(response) {
                if(response.status=='success'){
                    $('#todays_task').val(response.content);
                }                 
            },
        });
}); 


